css:
* {
	margin:0;
	padding:0;
	border:0;
	outline:0;
	font-size:inherit;
	font-weight:normal;
	vertical-align:baseline;
	background:transparent;
	color:inherit;
	text-decoration:inherit;
	text-size-adjust:none;
	-moz-text-size-adjust:none;
	-webkit-text-size-adjust:none;
	-ms-text-size-adjust:none;
}

body, html {
	-webkit-font-smoothing:antialiased;
	-moz-osx-font-smoothing:grayscale;
	width:100%;
	font-weight:normal;
	font-style:normal;
}

body {
	font-size:20px;
	background:#eee;
	font-family:monospace, sans-serif;
	font-size:20px;
	line-height:1.5;
	color:#444;
}

html {
	height:100%;
}

h1,h2,h3,h4,h5 {
    margin:1em 0 0 0;
	font-weight:bold;
}
h1 {
	font-size:40px;
}
h2 {
	font-size:32px;
	border-bottom:1px solid #888;
	padding-bottom:10px;
}
h3,h4,h5 {
	font-size:24px;
}

p {
	margin-top:1.5em;
	margin-bottom:1em;
}

em {
	font-style:italic;
}

ul {
	padding-inline-start: 40px;
}

strong,
a {
	font-weight:bold;
}

a:hover {
	text-decoration:underline;
}


.main {
	margin:20px auto 60px auto;
	width:960px;
}


.oldnew-links {
	width:960px;
	height:50px;
	padding-bottom:10px;
	position:relative;
	font-size:16px;
}

.old-link {
	left: 0;
	top: 10px;
	position:absolute;
	background:#444;
	color:#eee;
	padding:4px 8px;
	border-radius: 8px;
}

.new-link {
	right: 0;
	top: 10px;
	position:absolute;
	background:#444;
	color:#eee;
	padding:4px 8px;
	border-radius: 8px;
}

.old-link:hover,
.new-link:hover {
	text-decoration:none;
	background:#888;
}

.model-container {
	padding-top:20px;
}

.model-view {
	display: inline-block;
	width:75%;
	height:480px;
}

.model-download {
	display: inline-block;
	width:25%;
}

.model-download a {
	display: inline-block;
}

.bloglink {
/*
	height: 100px;
*/
	display: block;
	padding-bottom: 20px;
}

.bloglink img , .bloglink video , .bloglink audio {
    max-width:500px;
    height:auto;
    width:auto;
}

.bloglink-icon {
	width: 100px;
	height: 100px;
	display: inline-block;
}

.bloglink-date {
	display: inline-block;
	width: 150px;
	line-height: 100px;
	vertical-align: top;
	padding-left: 20px;
}

.bloglink-title {
	display: inline-block;
	width: 500px;
	vertical-align: top;
	padding-left: 20px;
	font-weight: bold;
/*
	height:100px;
*/
	overflow:hidden;
}

.blog_list_wrap {
	padding:20px 0;
}

table {
	display: block;
    max-width: 100%;
    border-spacing: 15px 10px;
}



.crumb-wrap {
	margin:0px auto;
	width:960px;
	padding:20px 0;
	text-align:right;
}

.crumb-slash {
	margin:0px 20px 0px 20px;
}

.crumb {
}

.utter_wrap {
	padding-top:30px;
}


.blogpost img {
	width:100%;
}

/*	PHONES & STUFF
	============================================= */

@media only screen and (-webkit-min-device-pixel-ratio: 1.5),
only screen and (-o-min-device-pixel-ratio: 3/2),
only screen and (min--moz-device-pixel-ratio: 1.5),
only screen and (min-device-pixel-ratio: 1.5) {

* {
	width:100%; 
	height:auto; 
	min-height:1px; 
	max-height:999999px;
}

body {
	font-size:3rem; 
	padding:30px 50px;
}

h1, h2 {
	font-size:4rem;
}

li {
	list-style-position:inside;
}

.old-link,
.new-link {
	width:initial;
	font-size:2.5rem;
	padding:4px 14px;
}

.bloglink {
	height:initial;
}

.bloglink-title {
	width:initial;
}

.bloglink-date {
	width:initial;
	padding-right:20px;
}

.model-download {
	padding-left:20px;
	box-sizing:border-box;
}

.model-download a {
    display:block;
    line-height:1em;
    padding-bottom:15px;
}

}

