html {
	font-size:16px;
}

a:link, a:visited, a:hover, a:active {text-decoration:none;}

html				{background:url('{_dirname}/bad_logo.png')center center no-repeat fixed, #000; background-size:cover;}
.cake_bars_wrap		{display:none;}
body				{width:100%; height:100%; background-color:transparent; font-family:monospace; font-size:12px;}
.cake_body			{width:100%; height:100%; position:relative;}
.default			{width:960px; margin:0 auto; text-align:center;}
.default.dont		{padding:20px 0; margin-top:60px; color:#fff; text-shadow:1px 1px 0px rgba(0,0,0,1); width:100%; background:url('{_dirname}/purps_back.png')repeat-x bottom; min-height:250px;}

p					{padding:10px 0;}
.box a.contact		{color:#539DFF;}

.alert				{display:block; width:100%; padding:10px 0; margin:0 auto; background-color:#b72f24; color:#fff; font-weight:bold; text-align:center; font-size:20px;}

table.notes			{width:1240px; margin:0 auto; padding-top:40px; table-layout:fixed;}
table.notes td		{padding-bottom:15px; padding-right:0; vertical-align:top; box-sizing:border-box;}
table.notes td.last	{padding-right:0;}
table.notes td.greet	{padding-right:0;}
.box				{width:400px; padding:40px 20px; color:#fff; box-sizing:border-box; margin:0 auto;}

.box.d				{background-color:rgba(0,0,0,0.8); width:810px; padding:20px;}
.box.e				{background-color:rgba(47,47,60,0.7); padding:20px;}

.box.x				{width:100%; font-size:1.6em; line-height:1.45em; padding:40px 120px;}
.box.x div			{text-align:center; margin-bottom:40px;}
.box.y				{padding:20px; font-size:1.2em; line-height:1.45em;}

.box a				{color:#DBFF00; display:inline-block; background-color:#000; padding:10px 15px; border-radius:10px; border:1px dashed #ccc;}
.box.d a			{color:#539DFF; font-size:1.2em; line-height:1.45em;}
.box a:hover		{background-color:#555;}
.box span			{color:#ff3f8b; font-weight:bold; font-size:1.05em;}

.box.d a.get		{display:block; padding:10px; background-color:#ff00ff; color:#000; text-align:center; font-size:1.3em;}
.box.d a.get.s		{background-color:#37BEFF;}
.box.d a.get:hover	{text-decoration:none; background-color:#DBFF00;}

p i					{font-size:0.8em; font-style:italic;}

.cal				{position:absolute; right:0; opacity:0.85;}

.dont span			{width:80%; display:block; margin:0 auto;}


.twerp 				{position:fixed; top:0; left:0; width:0; height:0; border-top:900px solid rgba(0,0,0,1); border-right:900px solid transparent; z-index:-1;}

a.hello				{display:inline-block; padding:20px; color:#FFF; background-color:#070707; margin-top:40px;}
a.hello:hover		{background-color:#44265F; text-decoration:none;}

twitterwidget		{width:300px; max-width:300px;}

.banner				{width:820px; padding:8px;}

@media screen and (max-width:1024px) {
html	{background-attachment: fixed; background-image:url('{_dirname}/bad_logo.png');  background-position:center center; background-repeat:no-repeat;}
body	{font-size:24px;}
.cake_body			{max-width:100%;}
table.notes td		{display:block; width:960px; padding-right:0;}
.box, .box.d		{width:960px; padding:40px 20px; margin:0 auto;}
.box.c iframe		{width:100%; height:960px;}
.cal iframe			{width:100%; height:170px;}
.box iframe			{width:100%; height:960px;}

.box.x				{padding:40px 60px;}


table.notes			{width:100%; margin:auto; table-layout:fixed; padding-top:200px;}
.cal				{display:block; width:100%;}
p					{padding:40px 0; line-height:1.4em; font-size:1.0em;}
div img				{width:100%;}
.box.d a.get		{font-size:0.6em;}
.dont span			{font-size:1.2em;}

.banner				{width:100%; padding:0;}
.banner	img			{width:100%;}
}
